import React from 'react';
import { Link } from 'react-router-dom';
import { Wrapper } from './cardItem.styled';


const CardItem = (props) =>{
    return (
        <Wrapper>
            <Link to= {`/product/${props.id}`}> </Link>
            <img style={{ borderRadius:'7px', width:20}} src={props.imageSrc}/>
            <p>{props.title}</p>
            <p style={{color:"gray"}}>{props.text}</p>
            <p style={{color:"#CE4E45"}}>${props.price}</p>
            <button style={{width:"100px", height:"30px", borderWidth:"0", borderRadius:"3px"}}>Buy now</button>
        </Wrapper>
    )

};

export default CardItem;
