import { styled } from "styled-components";

export const Wrapper = styled.div`
    display: flex;
    flex: 1;
    width: 80%;
    margin-left: 10%;
    font-family:"Roboto";
    font-size: 24px;
    color: white;
`;
export const ImageWrapper = styled.div`
    width: 50%;
`;
export const TextWrapper = styled.div`
    display: flex;
    width:50%;

    >p{
        margin-right: auto;
    }
`;
