import React from 'react';
import { Link } from 'react-router-dom';
import { Wrapper, CardItemWrapper } from './cardItem.styled';


const CardItem = (props) =>{
    return (
        <Wrapper>
            <CardItemWrapper>
            <Link to= {`/product/${props.id}`}> </Link>
                <ImageWrapper>

                </ImageWrapper>
                    <img style={{ borderRadius:'7px', width:200, height:200}} src={props.image}/>

                <div>
                    <p style={{color:"gray", fontSize:20}}>{props.title}</p>
                </div>
                <div>
                    <p style={{color:"gray", fontSize:14}}>{props.text}</p>
                </div>
                <div>
                    <p style={{color:"#CE4E45"}}>${props.price}</p>
                </div>
                <div>
                    <button style={{width:"100px", height:"30px", borderWidth:"0", borderRadius:"3px"}}>Buy now</button>
                </div>
            </CardItemWrapper>
        </Wrapper>
    )

};

export default CardItem;
